package com.shashi;

public class PrePost {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=10;
		System.out.println(a); //10
		System.out.println(a++);//10 //post-increment => (print, increase)
		System.out.println(++a);//12
		System.out.println(a--);//12
		System.out.println(--a);//10
		System.out.println(a);//10


	}

}
