package com.shashi;

public class Bitwise {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println(true & false);
		System.out.println(true | false);
		System.out.println(true ^ false);
		// We can apply bitwise operator on integral types also.
		System.out.println(4 & 5);
		System.out.println(4 | 5);
		System.out.println(4 ^ 5);
		
		

	}

}
